INSERT INTO games (id, name, summary, release) VALUES (17041, 'Sentinel 3: Homeworld', 'Take the fight to the alien homeworld and unleash a massive arsenal of weaponry. Get up close and personal on the battlefield in your power armor to execute devastating attacks and support your defences.', '2013-12-31') ON CONFLICT DO NOTHING;
