INSERT INTO games (id, name, summary, release) VALUES (5148, 'Sega Superstar Tennis', 'Sonic and a superstar cast of fan-favourite characters serve up extreme arcade tennis on courts inspired by SEGA’s psychedelic gameworlds. Compete in madcap matches, crazy tournaments and wacky minigames in this wildly imaginative take on tennis.&#13;
