INSERT INTO games (id, name, summary, release) VALUES (17642, 'See No Evil', 'See No Evil is a dark, isometric puzzle game about sound manipulation. A harsh fantasy where the willingly blind are hostile to the nonconformist. Sometimes, the world seems darker with your eyes open.', '2014-06-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19075, 'Seduce Me the Otome', 'A romantic comedy visual novel centered around a girl named Mika Anderson, who gets entangled in the affairs of incubi.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24642, 'Security Hole', 'Security Hole is a challenging spatial puzzle with a cyberpunk story. You become a hacker who passes 3D virus blocks through security holes to save humanity. The game mechanics look like a workout tool for spatial thinking.', '2016-09-27') ON CONFLICT DO NOTHING;
