INSERT INTO games (id, name, summary, release) VALUES (10569, 'Samantha Swift and the hidden roses of Athena', 'On a quest to recover the Scepter of Zeus for The Museum of Lost Secrets, archeologist and adventurer Samantha Swift stumbles upon one of the greatest archeological finds of all time! Unfortunately, she also runs into greedy treasure hunter Ravena Stryker.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10570, 'Samantha Swift and the golden touch', 'When Samantha''s new benefactor asks her to track down Alexander the Great''s Scroll of Wealth, the opportunity seems too be good to be true—searching for the scroll while collecting amazing finds for the Museum of Secrets Lost!', null) ON CONFLICT DO NOTHING;
