INSERT INTO games (id, name, summary, release) VALUES (12757, 'Sabre Team', 'A Sabre Team is a squad of four elite soldiers charged with some of the toughest hostage-rescue, building-storming and the like that Western forces have ever attempted. In this turn-based strategic simulation you must choose a team of four of these from the 8 available, and take them to victory in five increasingly tough missions. Even before you get into the main gameplay, there is a lot of strategy involved in choosing weaponry, as they make different amounts of noise when used (reducing the stealth potential) and their ammunition is of varying weight.
INSERT INTO games (id, name, summary, release) VALUES (26354, 'Saboteur!', null, '1986-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12964, 'Saboteur II', 'Saboteur II: Avenging Angel is a sequel to the 1985 video game Saboteur. Saboteur II was one of the first action-adventure games to feature a female protagonist.', '1987-12-30') ON CONFLICT DO NOTHING;
