INSERT INTO games (id, name, summary, release) VALUES (14721, 'Run or Die', 'Run or Die is a fast paced Endless Runner that focuses on movement based abilities (and a huge Ion Cannon)! Players must make decisions in the blink of an eye and react quickly in order to make their way through a hazardous city environment where every run is different! Can you make it to the Lab to save Flow?', '2014-12-10') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22358, 'Run Saber', 'Run Saber is a 1993 side-scrolling action game developed by Horisoft and published by Atlus for the Super Nintendo Entertainment System. The game was released in North America on June 8, 1993, and in Europe in 1993. Run Saber features side-scrolling fighting as the two main heroes, Allen and Sheena, the Run Sabers, fight to save the planet. The gameplay is very similar to Strider.', '1993-06-07') ON CONFLICT DO NOTHING;
