INSERT INTO games (id, name, summary, release) VALUES (25582, 'Rocket Drift - Extreme gravity adventure', 'Rocket Drift is a platformer where you control small rocket and fly it through all sorts of obstacles to the designated landing platform for safe landing. Gravity and inertia makes this game really challenging.', '2016-10-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10781, 'Rockefeller: The Black Gold', 'Rockefeller: The Black Gold makes you become the ultimate Oil Mogul. Take over the role of an oil industrialist and search for oil in different places all over the world, build pipelines, refineries, petrochemical plants and factories. Export the oil to make lots of cash or use it to manufacture oil based products based on the needs of local markets and cities. It is up to you to satisfy your investors! Research various technologies that give you the ability to manufacture new products, reduce the cost of transportation and boost the development of cities.
