INSERT INTO games (id, name, summary, release) VALUES (26722, 'Revolve', 'Revolve is a Triggeractive Platformer. You play as Reevo, drone on wheels who is trying to escape a dystopian world. You can use speed boosts, brake, jump or switch gravity.INSERT INTO games (id, name, summary, release) VALUES (20298, 'Revolution X', null, null) ON CONFLICT DO NOTHING;
