INSERT INTO games (id, name, summary, release) VALUES (17541, 'Reversi', 'Reversi is an ageless game that is simple to learn and hard to master.  Place tiles surrounding your opponents tiles to flip them to your color.  Get to the end with more tiles of your color and you are the winner.', '2015-02-03') ON CONFLICT DO NOTHING;
