INSERT INTO games (id, name, summary, release) VALUES (15385, 'Reverse x Reverse', '"Reverse x Reverse is the latest game from desunoya, creator of Tobari and the Night of the Curious Moon. This game is a cute &amp; quirky challenging puzzle platformer, where you control two girls, Code and Rithm, as they traverse a virtual world and complete 81 brain-bending levels."', '2015-08-15') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17864, 'Revenge: Rhobar''s myth', 'Join the worlds best wizard academy and embark on an exciting, mysterious journey. Learn 4 different and mighty spells to change the world around you, experience a breathtaking plot about the lost wizard Rhobar or challenge yourself in the freeroam mode! Combine the use of the 4 unique Spells Auraco, Luminos, Terramus and Ignis so solve magical puzzles and find the ideal route in the Spell-Challenges. Set your own highscore and rise the bar for other wizards to a new level.', '2015-08-01') ON CONFLICT DO NOTHING;
