INSERT INTO games (id, name, summary, release) VALUES (16431, 'Retro/Grade', 'Play a classic Shoot ''Em Up... in reverse! Navigate deadly enemy waves and un-fire attacks in Retro/Grade, a mind-bending rhythm game by 24 Caret Games. Pick up a gamepad or a guitar controller and help Rick Rocket restore the continuity of space-time, before a temporal anomaly rips the universe a new time-hole.', '2013-02-02') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17933, 'Retro-Pixel Castles', 'A godlike village simulator designed to bridge the gap between the complexities of village simulation with the simplicity of classic 1990s real-time strategy. Inspired by games like Towns, Gnomoria, Warcraft, Banished and Dwarf Fortress.', '2014-08-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3229, 'Retro World', 'Retro World takes you on a fantastic journey to a parallel universe where video games come to life. Experience 30 years of gaming evolution, from the early pong days to the modern games of today. Attend midnight game launches or test your skills in competitive in-game tournaments. Read in-game magazines for tips, tricks and reviews, or for the latest rumors about the new Gametron 9000 XZ. Essentially Retro World let''s you relive your gaming childhood as a retro sprite.', null) ON CONFLICT DO NOTHING;
