INSERT INTO games (id, name, summary, release) VALUES (847, 'Resident Evil 5', 'Resident Evil 5 places you into the shoes of fan favorite, Chris Redfield. You are joined by Sheva Alomar, an African special agent tasked with investigating the epidemic which has broken out in Africa - a new bio-terrorism threat which is transforming the people and animals of the city into mindless, maddened creatures.', '2009-03-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21533, 'Resident Evil 4: Wii Edition', 'Resident Evil 4 is a survival horror third-person shooter video game developed by Capcom Production Studio 4 and released by multiple publishers, including Capcom, Ubisoft, Nintendo Australia, Red Ant Enterprises and THQ Asia Pacific. The sixth main entry in the Resident Evil horror series, the game was originally released for the GameCube in January 2005 in North America and Japan, and in March 2005 in Europe and Australia, and later released for the Wii on May 31st, 2007 in Japan and on June 19th, 2007 in the United States.', '2007-05-30') ON CONFLICT DO NOTHING;
