INSERT INTO games (id, name, summary, release) VALUES (8681, 'Redline', 'It''s 2066 and the world is a different place. While the privileged insiders live in fertile domed cities, the rest of us fight for survival in the wastelands. Rival gangs battle for supremacy, turf control and valuable resources. In order for a young man to succeed he must make the right choices about his future. That''s why you want to join the Company.
INSERT INTO games (id, name, summary, release) VALUES (18297, 'Redjack: Revenge of the Brethen', null, '1998-07-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25576, 'Redirection', 'Redirection is a challenging 3D puzzle game set in Outer Space. As the Computer of an Interstellar Starship, only you can rescue it’s missing Robot crew from the perils of an unexplored asteroid belt. Across 45 hazard-filled levels, you must use the ship''s teleporter to influence the paths of stranded Robots, and "redirect" them back to safety. As you progress through the game, you’ll discover new twists and mechanics, and uncover an exciting science fiction mystery story.', '2016-10-23') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25954, 'Redie', 'Redie is a challenging singleplayer 3D top-down shooter. Fight an army of well-trained terrorists and soldiers in action-packed close quarters combat. Prepare to (re)die.', '2016-11-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25098, 'Redcon', 'Command your own battle fortress! Assemble the mightiest artillery force and bombard your foes into oblivion! Lead the Empire State offensive against Traitor General and his foul rebellion.', '2016-04-22') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24836, 'Redcat: De Razende Rekenrace', 'As Redcat, you use your math skills to beat the evil Brutus de Bulldog and Max de Muis on a set of race tracks. This game contains several different modes where you can test your math skills.', '1997-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4084, 'RedCard', null, '2002-04-15') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12744, 'Red Zone', 'Red turns to green as Psygnosis go for the pole position with Red Zone: their hell-for-leather bike race simulator. Straddle a mighty machine, kickstart her into screaming action and take control of one of the fastest vehicles on two wheels. Burn rubber as you fight for first place and strive for the thrill of taking the checkered flag ahead of the pack...
INSERT INTO games (id, name, summary, release) VALUES (22646, 'Red Turn: The Road to Berlin 1943-45', null, null) ON CONFLICT DO NOTHING;
