INSERT INTO games (id, name, summary, release) VALUES (9411, 'Red Baron History', 'During World War I, when everyone was just running around and struggling to not be killed, there were few soldiers - fighter pilots -, trying to resist and fight to stay alive. Even being volunteers and courageous, most of them just get killed.  INSERT INTO games (id, name, summary, release) VALUES (21266, 'Red Baron Arcade', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1934, 'Red Baron', 'Experience the thrill of flying World War I bi- and triplanes. Defend yourself in spectacular aerial dogfights whilst enemies assault you from all sides. Carry out bombing missions, negotiate enemy AAA fire and outwit your opponents with smart tactical moves. Red Baron Pack offers exciting air-to-air and air-to-ground combat in an age when wooden bi-planes and tri-planes ruled the air.', '1989-12-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18469, 'Red Baron', 'Red Baron is an arcade game developed by Atari, Inc and released in 1980. A first-person flight simulator game, the player takes the role of a World War I ace in a biplane fighting on the side of the Allies.', '1980-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15708, 'Red Ash: The Indelible Legend', 'Red Ash: The Indelible Legend is an upcoming video game in development by Comcept and HYDE, Inc. The game, based on a concept by Keiji Inafune, is heavily influenced by the Mega Man Legends series by Capcom, which many of the Comcept staff had worked on previously', '2017-05-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13749, 'Red Arrows', null, null) ON CONFLICT DO NOTHING;
