INSERT INTO games (id, name, summary, release) VALUES (16626, 'Recruits', 'Recruits is a brutal fast paced top down shooter that puts the lives of countless amounts of soldiers in your command. Lead your men to victory and see them progress through the ranks, show them defeat and they will be lost forever.', '2014-06-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9412, 'Recovery Search and Rescue Simulation', 'As a new recruit to Rescue Team One, you’ll need to use your expert tracking skills to locate a variety of important objects and missing persons who have stumbled into the harsh dangers of your national park.
INSERT INTO games (id, name, summary, release) VALUES (7157, 'Record of Agarest War Zero', 'From the void, the gods of darkness and light created Agarest, a most perfect and divine world. As the generations passed, paradise sadly fell to the ravages of time. War darkened the skies and Agarest soon became a torturous hell as the gods divided and immense armies clashed, desecrating the once beautiful land…
INSERT INTO games (id, name, summary, release) VALUES (7158, 'Record of Agarest War', null, '2007-09-26') ON CONFLICT DO NOTHING;
