INSERT INTO games (id, name, summary, release) VALUES (12524, 'Recettear: An Item Shop''s Tale', '"Recettear is an item-shop-focused role-playing game. That means that the core gameplay revolves around running your item store. You will find or purchase items, sell them to townsfolk, and manage your store with an eye toward repaying a debt each week. The game is beaten when the debt is completely repaid."', '2007-12-20') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8141, 'Receiver', null, '2013-04-28') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16724, 'Rebuild 3: Gangs of Deadsville', 'Rebuild 3 is a strategy sim game with a good helping of narrative and dark humor. It''s a thoughtful game that''ll make you fond of your survivors before you send them to their deaths for the greater good. As you expand your fort to new buildings, you''ll decide between farms or fortifications, housing or hospitals, and make tough decisions to fend off raiders, illness, starvation, and madness. And the dead are still out there wandering the streets, hungrier than ever.', '2015-04-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16977, 'Rebound', 'Rebound is a 2 player arcade game by Atari Inc., originally released in February 1974. The game simulates a volleyball match, having players volley a ball back and forth over a net with their paddles.', '1974-01-31') ON CONFLICT DO NOTHING;
