INSERT INTO games (id, name, summary, release) VALUES (18420, 'RealSports Volleyball', 'Realsports Volleyball is a 1982 volleyball video game made by Atari for the Atari 2600 platform.', '1982-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18419, 'RealSports Tennis', 'RealSports Tennis is a tennis simulation video game developed by Atari, Inc. for the Atari 2600 and Atari 5200, and published in 1983.', '1983-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18418, 'RealSports Football', 'RealSports Football is a 1982 football video game made by Atari for the Atari 2600, Atari 5200 and Atari 8-bit computer platforms. RealSports Football presents a greatly simplified version of football for play. Players in the game cannot go out of bounds or fumble the football, and there are no penalties in the game. Also, touchdowns automatically score 7 points; there is no kicking for extra points.', '1982-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18417, 'RealSports Boxing', 'RealSports Boxing is a boxing based video game developed by Atari and released in 1987 for the Atari 2600. It is part of the RealSports series of games from Atari. The game has a side view of the ring, allowing the player to move up and down, as well as from left to right. There are four selectable characters in the game, Lefty O''Leary, Jabbin'' Jack, Macho Man, or Iron Fists. The aim of the game is to knock out the opposition by filling up a bar at the bottom of the screen which allows the player to deliver the knock out blow. The game can be played with two players simultaneously.', '1987-12-30') ON CONFLICT DO NOTHING;
