INSERT INTO games (id, name, summary, release) VALUES (10674, 'Reading Blaster: Invasion of the Word Snatchers', 'Illitera has stolen all the words from Earth. Your mission is to catch Illitera''s spaceship and gain entry to the main control room so the words can be returned.', '1994-12-30') ON CONFLICT DO NOTHING;
