INSERT INTO games (id, name, summary, release) VALUES (19700, 'ReROLL', 'ReROLL is a cancelled PC Online Action RPG in a true open world settings. Ex-Ubisoft had a plan to map the entire planet with drones to generate its world. The developers didn''t raise enough money so the project was cancelled.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18206, 'RePete', 'RePete is a pixel art puzzle platformer about time travel and self-cooperation. You play as Pete, a special guy that can loop back in time to help his past self solve problems, collect gems, and reach new heights.', '2015-09-12') ON CONFLICT DO NOTHING;
