INSERT INTO games (id, name, summary, release) VALUES (20032, 'Rayman Adventures', 'The enchanted forest is in trouble; the ancient eggs that sustain the sacred tree have been stolen and scattered across the world.', '2015-03-11') ON CONFLICT DO NOTHING;
