INSERT INTO games (id, name, summary, release) VALUES (18698, 'Rage Racer', 'Rage Racer (レイジレーサー) is the third title in the Ridge Racer series of racing games on the Sony PlayStation. Created by Namco, it was first released in Japan on 3 December 1996.', '1996-12-02') ON CONFLICT DO NOTHING;
