INSERT INTO games (id, name, summary, release) VALUES (22417, 'Ragdoll Runners', 'Ragdoll Runners is a physics-based athletics game where you control independently the two legs of an athlete in 8 events of different types: sprint, long jump, triple jump, hurdles. Play against the computer, with 3 friends on the same PC or online against other players'' records.', '2016-07-12') ON CONFLICT DO NOTHING;
