INSERT INTO games (id, name, summary, release) VALUES (6516, 'Radiant Historia', 'Radiant Historia (ラジアントヒストリア) is a role-playing video game developed and published by Atlus for the Nintendo DS. The game''s development team mostly consists of Atlus staff who previously worked on Megami Tensei games like Nocturne, Persona 3 and Strange Journey as well as the Etrian Odyssey series. The development team also includes other members who had previously worked on Radiata Stories. The game was released on November 3, 2010 in Japan, and in North America on February 22, 2011.', '2011-02-21') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17442, 'Radiant Defense', 'Radiant Defense is a tower defense game set in a vibrant universe invaded by countless alien hordes. Build your space fortress any way you wish, set up wide variety of weapons and traps and let the invasions begin!', '2014-06-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17986, 'Radial-G : Racing Revolved', 'Climb into the cockpit of a futuristic race craft and tear up the anti-gravity track. Offering single &amp; multi-player action, Radial-G delivers full immersion racing on tubular tracks featuring gut-wrenching twists, jumps, splits &amp; unique inverted racing. Also supports Oculus VR.', '2016-03-27') ON CONFLICT DO NOTHING;
