INSERT INTO games (id, name, summary, release) VALUES (10380, 'Race 07', 'RACE 07 - The Official WTCC Game features the full FIA WTCC season of 2007 including all cars and tracks, as well as several new support classes and tracks from all over the world. The game also features the full WTCC season of 2006 which was included in the games predecessor RACE - The WTCC Game, released in 2006 on PC.
