INSERT INTO games (id, name, summary, release) VALUES (17590, 'RC Mini Racers', 'RC Mini Racers is a fast paced and explosive racing game with 20+ remote controlled mini cars, lots of missiles and mines, turbos for an extra boost as well as great hard rock music. More than 20 cars can be unlocked within the game and the car pimp shop lets you customize you RC car by changing the color, the wheels and the antenna.', '2015-06-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (25139, 'RAMSAK', 'RAMSAK is a game similar to Pac-Man, but completely rendered in ASCII art. The player (a white smiling face) travels on a field, trying to collect all dots and symbols and escaping evil Red Faces. There are also destructible (light-blue) and indestructible (dark blue) blocks on field.
INSERT INTO games (id, name, summary, release) VALUES (10748, 'RADical ROACH', 'RADicalROACH is a fast paced shoot em’ up, set minutes after nuclear hell falls from the sky engulfing all but a few tough little bugs. Play as the super little radiated roach with new found powers, get to safety before the army of fleas get you.', null) ON CONFLICT DO NOTHING;
