INSERT INTO games (id, name, summary, release) VALUES (10867, 'RACE Injection', 'RACE Injection is the final chapter in the RACE 07 series of games, ending the award winning series on a high note. RACE Injection contains 23 new unique car models and boasts 9 new tracks from across the globe.', '2011-11-17') ON CONFLICT DO NOTHING;
