INSERT INTO games (id, name, summary, release) VALUES (17726, 'Quantum Rush Champions', 'Quantum Rush: Champions is an action-packed, futuristic racing game for PC. This special genre had been neglected for far too long. Now, Quantum Rush takes over', '2014-11-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (5996, 'Quantum Redshift', 'Set 100 years in the future, Quantum Redshift lets players take to the track in a no-holds-barred combat pod racing tournament with the chance of reaching speeds up to 900 miles per hour. You can bet it''ll be hard to see any stop signs going that fast.', '2002-09-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26604, 'Quantum Lake', 'In Quantum Lake  the player drives a growing party of bright polygonal fishes through the depths of complex labyrinths inside a cosmic lake.&#13;
