INSERT INTO games (id, name, summary, release) VALUES (18604, 'Quadra-Doodle', 'Quadra-Doodle is one of the launch titles for the Fairchild Channel F console and is one of four games on the system''s Cartridge No.1. It is an automatic doodle-program, but the "doodleing" is mirrored twice so there are four dots at the same time, creating a caleidoscope-effect. The player can take over at any time and doodle by hand (with one dot), also clear the screen, change the size of the paintblock and color as well while the computer keeps plotting.', '1976-10-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (6540, 'Quad Desert Fury', null, '2003-09-29') ON CONFLICT DO NOTHING;
