INSERT INTO games (id, name, summary, release) VALUES (11847, 'Puella Magi Madoka Magica: The Battle Pentagram', 'Puella Magi Madoka Magica: The Battle Pentagram is a 3D action game based on the Puella Magi Madoka Magica anime franchise. The game features daytime and nighttime sequences. At daytime, players can talk with the magical girls and strengthen their bonds, while at nighttime players battle witches, the results of which can affect your bonds with the magical girls.', '2013-12-18') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (11846, 'Puella Magi Madoka Magica Portable', 'Puella Magi Madoka Magica Portable is a Japanese roguelike role-playing game with visual novel elements. It is based on the Puella Magi Madoka Magica anime franchise. The game allows players to take many routes, changing the fate of the original storyline.', '2012-03-14') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10728, 'Puddle', 'Dive into Puddle, a game unlike any other where you have to guide a puddle of fluid by tilting the environment to the left or right! Play carefully taking friction, gravity and temperature into account, and by using the unique properties of each type of fluid (water, oil, nitroglycerin, molten lava...) in a range of original environments. Conserve as much fluid as possible when crossing a variety of obstacles such as burning passageways, carnivorous plants and electric currents!', '2012-01-24') ON CONFLICT DO NOTHING;
