INSERT INTO games (id, name, summary, release) VALUES (17731, 'Prophour23', 'Prophour23 is a fast-paced, replayable real-time strategy. It''s designed for short, quick plays. A single playthrough will take you about 15 minutes, if you''re good. But the next one will be different. And another one as well.', '2014-09-02') ON CONFLICT DO NOTHING;
