INSERT INTO games (id, name, summary, release) VALUES (16044, 'Project Freedom', 'Get your dream job with a company called Project Freedom. Become a space pilot and serve for the corporation. You''re going to get all the excitement and adventure you''ve ever wanted.', '2009-09-02') ON CONFLICT DO NOTHING;
