INSERT INTO games (id, name, summary, release) VALUES (19411, 'Project Altered Beast', null, '2005-01-26') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15964, 'Project Aftermath', 'Project Aftermath is the PAX10 winning Arcade RTS where you command up to four squads of soldiers in exciting battles in a retro-futuristic world. Equip your squads to suit your playing style, researching new items using the spoils of war; choosing from over 100 different weapons, armour, power-up augmentations and spell-like special attacks.', '2008-09-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26204, 'Project Abyss', 'Project Abyss is a 2D RPG survival game.
