INSERT INTO games (id, name, summary, release) VALUES (21769, 'Prinny 2: Dawn of Operation Panties, Dood!', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (15890, 'Princess Remedy in a World of Hurt', 'Remedy, a recent graduate from the Saturnian healing school, must descend to Hurtland and use her powers to heal everyone from stray ducks to dark lords, and finally cure the ailing Prince Hingst. It''s a short, whimsical adventure with plenty of heart(s).
INSERT INTO games (id, name, summary, release) VALUES (6539, 'Princess Natasha', null, '2006-10-23') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (27317, 'Princess Maker Refine', 'Princess Maker Refine revisits the classic first installment of the Princess Maker series, a mainstay of the simulation gaming genre with a long-lasting and dedicated fan base. Princess Maker combines simulation gaming with an interesting, in-depth story as it follows the tale of a young girl adopted by a warrior and raised to become a lady.', '2003-03-02') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (24980, 'Princess Maker 2 Refine', 'Princess Maker 2 Refine offers a unique life simulation gameplay where the player takes a role as a father for a young girl and raise her into adulthood. Players have freedom of choices on how to raise the child, but each choice will impact on the outcome of what she will become.', '2004-09-29') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4262, 'Princess Maker 2', null, '1995-11-05') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17221, 'Princess Isabella - Rise of an Heir', null, '2014-03-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17220, 'Princess Isabella - Return of the Curse', null, '2014-03-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (26525, 'Princess Edge: Dragonstone', 'Princess'' Edge - Dragonstone is a beat''em up inspired by Golden Axe, however unlike the usual beatemup it use the 3 planes system similar to Guardian Heroes. Ride your dragon &amp; unleash powerful magics upon your enemies to restore the peace to the land!', '2016-03-24') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21225, 'Princess Debut', null, null) ON CONFLICT DO NOTHING;
