INSERT INTO games (id, name, summary, release) VALUES (18633, 'Potty Pigeon', 'Percy The Potty Pigeon is a 1984 computer game developed by Shaun Hollingworth and released by Gremlin Graphics for the Commodore 64 and ZX Spectrum. The goal was to fly around and collect sticks to build your nest. The player could also deficate on cars, and make them crash.
