INSERT INTO games (id, name, summary, release) VALUES (25678, 'Political Animals', 'Political Animals is an election simulation game set within a fictional world populated by corrupt crocodiles and meritocratic mice. In a political contest where corruption is always around the corner, we want players to find out how incorruptible they really are.', '2016-11-01') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (4290, 'Policenauts', 'Policenauts is an interactive movie/graphic novel/adventure game with a hard science fiction storyline, written and directed by Hideo Kojima, and published by Konami. It was initially released for the PC-9821 computer platform in 1994, followed by remade versions for the 3DO in 1995, and the PlayStation and Sega Saturn in 1996. The game has never been officially released outside Japan, despite plans for an English localization of the Saturn version. On August 24, 2009 (in honor of the 46th birthday of the game designer, Hideo Kojima), an unofficial English translation patch was released onto the internet.
