INSERT INTO games (id, name, summary, release) VALUES (19679, 'Polandball: Can into Space!', 'The internet-born and beloved countryballs comics finally have a game to properly represent them, Polandball: Can Into Space! Break through Earth''s atmosphere to prove other countryballs once and for all they were wrong and conquer the final frontier!', '2016-06-15') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3028, 'Pokémon Yellow: Special Pikachu Edition', 'You''ve finally been granted your Pokemon trainer''s license, and now you''re on your way to becoming the world''s greatest Pokemon trainer! The shockingly cute Pikachu tags along behind you as you search the enormous world for monsters to train and evolve. Face off against Blastoise''s torrential water gun. Stand strong when facing Pidgeot''s stormy gust. Develop the ultimate Pokemon strategy to defeat the eight GYM leaders and become the greatest Pokemon master of all time!', '1998-09-11') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1512, 'Pokémon Yellow', 'An enhanced version of Pokémon Red and Blue Versions, Pokémon Yellow brings the video game experience closer to the Pokémon Anime by allowing you to start the game with your very own Pikachu following you around on-foot and collect the 3 other starters from strangers you meet around the region. However, your goal remains the same: Catch them all and become the ultimate Pokémon Master.', '1998-09-11') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2287, 'Pokémon Y', 'Your adventure in Pokémon X and Pokémon Y takes place in the Kalos region, home to beautiful skies and forests! The Kalos region is full of interesting places to visit and explore!
