INSERT INTO games (id, name, summary, release) VALUES (8353, 'Pokémon White Version 2', 'Pokémon WhiteVersion 2 and Pokémon Black Version 2 are the second and final paired versions of Generation V and are the sequels of Pokémon Black and White, respectively.
