INSERT INTO games (id, name, summary, release) VALUES (1557, 'Pokémon Ruby', 'Sequel to Pokémon Gold and Silver Versions (1999), Pokémon Ruby and Sapphire Versions offer 135 new Pokémon, more complex battling and training systems, new crime organizations, a longer and more involving campaign, upgraded graphics and an improved soundtrack to create a new and technically improved Pokémon experience.', '2002-11-20') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1561, 'Pokémon Red', 'Pokémon Red and Blue Versions offer a JRPG experience in which you capture and "evolve" your team of monsters by training them to higher levels. Choose one of 3 starter Pokémon and embark on a quest to catch them all - including the fabled legendary beasts -, take down a crime organization, overcome your rival, challenge 8 different Pokémon Gyms and defeat the Elite Four to become the ultimate Pokémon Master.', '1996-02-26') ON CONFLICT DO NOTHING;
