INSERT INTO games (id, name, summary, release) VALUES (1511, 'Pokémon Blue', 'Pokémon Red and Blue Versions offer a JRPG experience in which you capture and "evolve" your team of monsters by training them to higher levels. Choose one of 3 starter Pokémon and embark on a quest to catch them all - including the fabled legendary beasts -, take down a crime organization, overcome your rival, challenge 8 different Pokémon Gyms and defeat the Elite Four to become the ultimate Pokémon Master.', '1996-05-09') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18447, 'Pokémon Black and Blue', 'A browser game created by People for the Ethical Treatment of Animals (PETA), an animal rights organization based in the United States. It''s a parody of Pokémon Black and White created to spread attention about real-life animal rights and animal welfare concerns.', '2013-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8284, 'Pokémon Black Version 2', 'Pokémon Black Version 2 and Pokémon White Version 2 are the second and final paired versions of Generation V and are the sequels of Pokémon Black and White, respectively.
