INSERT INTO games (id, name, summary, release) VALUES (5573, 'Planet Explorers', 'Planet Explorers is an open world voxel based sandbox adventure RPG game set on a distant planet. The game uses a new OpenCL calculated voxel system to allow players to change the terrain in any way, create new objects such as weapons, vehicles, objects, and do it anywhere.', '2016-11-07') ON CONFLICT DO NOTHING;
