INSERT INTO games (id, name, summary, release) VALUES (21036, 'Planet Crashers', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (27120, 'Planet Colonization', 'The outstanding Real Time Strategy / Simulation with in-depth society management. Explore the planet, build and defend your colony. Trade products and weapons. Experience coexistence with alien lifeforms.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (18829, 'Planet Coaster', 'Planet Coaster is a construction and management simulation video game. Similar to its spiritual predecessor, the game allows players to build different theme park rides and roller-coasters. These player-created attractions can be shared through a mechanic called “global village”.', '2016-11-16') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14908, 'Planet Busters', 'Planet Busters is a new single-player game from Slam, combining puzzle-solving action with awesome firepower and spectacular explosions.
INSERT INTO games (id, name, summary, release) VALUES (16614, 'Planet Arkadia', 'Planet Arkadia defines the future of online science fiction games with action-oriented combat, customized personal story lines, epic dynamic events and no subscription fees!', '2011-05-25') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (21088, 'Planet 51', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17018, 'Planet', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (832, 'Planescape: Torment', 'The first part of Planescape: Torment takes place in Sigil, a city located atop an infinitely tall spire at the center of the multiverse, that connects the planes with each other via a series of portals. The city is overseen by the powerful Lady of Pain, while fifteen factions control different functions of the city related to each group''s world view. Every faction strives for further control of the city. The Nameless One can even join several of these factions during the game. The story eventually moves on to other planes, such as Baator and Carceri, where The Nameless One continues to discover more about his past.', '1999-12-11') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8604, 'Planes: Fire and Rescue', null, '2014-11-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10703, 'Plan it Green', 'You’ve been elected the Mayor of your hometown and have decided to take the barren environment and Plan it Green! Bring back the natural beauty of your city, and create a brighter, cleaner future. Build eco-homes and apply green upgrades, all while bringing new clean jobs and industry to your city. Increase your Greendex as you leave behind the ways of the past and create a beautiful, sustainable metropolis!', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (12717, 'Plan 9 From Outer Space', null, '1992-12-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (8585, 'Plain Sight', 'Plain Sight is an indie game that utilises basic mechanics and plays on them greatly, usually involving charging up attacks and gravity shifting as your jump around the levels. It is practically "a game about suicidal ninja robots".', '2010-04-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (7601, 'Plague Inc: Evolved', 'Plague Inc: Evolved is a unique mix of high strategy and terrifyingly realistic simulation. Your pathogen has just infected ''Patient Zero'' - now you must bring about the end of human history by evolving a deadly, global Plague whilst adapting against everything humanity can do to defend itself.', '2016-02-17') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (2065, 'Plague Inc.', 'As a new disease, it is your job to destroy the entire human species by evolving to a deadly plague.', '2012-05-25') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (22338, 'Pizza vs. Skeletons', null, '2012-02-15') ON CONFLICT DO NOTHING;
