INSERT INTO games (id, name, summary, release) VALUES (14453, 'Pizza Tycoon', 'Your goal in Pizza Tycoon is to build an empire of pizza parlors while dealing with competing restaurants, crooked police officers, or even ruthless gangsters! The object of the game is to control a minimum of five percent of the market share in all ten cities.
