INSERT INTO games (id, name, summary, release) VALUES (22154, 'Pixel Defenders Puzzle', null, null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (16836, 'Pixel Boy and the Ever Expanding Dungeon', 'Pixel Boy is an Action-RPG Shoot-em-up with dynamically generated stages across four acts each with an epic boss finale. Players can craft custom armours as well as stack and combine power ups into over 6000 different powerful weapons to overcome the dangers of the dungeon.', '2014-04-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9374, 'Pix the Cat', 'Pix the Cat features many different game modes including single and multiplayer modes, some changing the theme and aesthetic. Including Arcade, Nostalgia, Laboratory, and Arena modes, you''ll play as Pix - who plays Pix - heading deeper and deeper into levels upon levels in a Snake-meets-Pac-Man digital world. 
INSERT INTO games (id, name, summary, release) VALUES (17285, 'Pivvot', 'Pivvot is a thrilling game of strategic avoidance that will consistently test and challenge your ability to make quick, impulsive decisions. 
INSERT INTO games (id, name, summary, release) VALUES (24660, 'Pitstop II', null, '1984-12-30') ON CONFLICT DO NOTHING;
