INSERT INTO games (id, name, summary, release) VALUES (14916, 'Pirates, Vikings, and Knights II', 'Pirates, Vikings, and Knights II boasts a unique three-team symmetry and a myriad of colorful characters to promote exciting, strategic, and fun gameplay. It features several game modes, each requiring its own special brand of cunning to master. Do battle on a tropical island for control of bountiful treasure. Appease your gods by spilling the blood of your enemies deep within a Mayan temple. Conquer your foes by taking control of precious badlands territory.', '2010-01-04') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14978, 'Pirates, Vikings and Knights', null, null) ON CONFLICT DO NOTHING;
