INSERT INTO games (id, name, summary, release) VALUES (17007, 'Paws: A Shelter 2 Game', 'Paws is a standalone, single player, adventure, platformer set in the world of Shelter 2 with loss and friendship as its central themes.', '2016-03-23') ON CONFLICT DO NOTHING;
