INSERT INTO games (id, name, summary, release) VALUES (6271, 'Pathway to Glory: Ikusa Islands', 'Commanded by an old navy veteran McDouglas, a squad of allied soldiers take on the horrors of the Pacific Theatre. Only their strong ties to each other and to the seasoned veteran commander can keep them alive.', '2005-11-07') ON CONFLICT DO NOTHING;
