INSERT INTO games (id, name, summary, release) VALUES (20981, 'Paranautical Activity', 'Paranautical Activity combines the classic FPS action of games like Doom and Quake, with the randomness and difficulty of modern roguelikes like Binding of Isaac and Spelunky.', '2014-10-19') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (13545, 'Parallax', 'Parallax is a first-person puzzle game with two overlapping worlds. Weave back and forth between black and white as you try to be in the right place, in the right dimension, at the right time. Use switches, boosters and gravity to help you reach the exit. Can you conquer every mind-bending level?', '2015-03-09') ON CONFLICT DO NOTHING;
