INSERT INTO games (id, name, summary, release) VALUES (21999, 'Parachronism: Order of Chaos', 'Parachronism: Order of Chaos is a 3D Action RPG with social simulation system as one of its elements. This game is created by a new indie game developers around the world.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (9926, 'ParaWorld', 'The game-concept is similar to "Age of Empires", which is a real-time strategy game. ParaWorld features food, wood, and stone as resources. Food can be picked from bushes, hunted from animals, fished from the sea, or farmed. Stone is located in limited deposits, and wood, cut from trees, is generally scattered throughout a map. Battles can occur on both land and sea.
INSERT INTO games (id, name, summary, release) VALUES (7388, 'Papo & Yo', 'Quico’s best friend, Monster, is a huge beast with razor-sharp teeth, but that doesn’t scare Quico away from playing with him. That said, Monster does have a very dangerous problem: an addiction to poisonous frogs. The minute he sees one hop by, he’ll scarf it down and fly into a violent, frog-induced rage where no one, including Quico, is safe. And yet, Quico loves his Monster and wants to save him.
INSERT INTO games (id, name, summary, release) VALUES (2935, 'Papers, Please', 'Papers, Please was developed by Lucas Pope beginning in November, 2012 using the Haxe programming language and the NME framework, both open-source. As an American living in Japan, Pope dealt with immigration in his international travels and thought the experience, which he describes as "tense", could be made into a fun game. Before release, Pope had set up a name submission form for the public, where people could submit their own names to be randomly assigned to scripted characters in the game. Papers, Please was submitted to Steam Greenlight on April 11, 2013 and was greenlit on May 1.', '2013-08-07') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (19370, 'Paperboy 64', 'A 3D version of the arcade classic for the Nintendo 64', '2000-12-30') ON CONFLICT DO NOTHING;
