INSERT INTO games (id, name, summary, release) VALUES (9568, 'Pako', 'Pako is a car chase simulator that focuses on you evading cops. The aim of the game is to survive as long as possible without crashing and ending the game.', '2013-12-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (17716, 'Pajama Sam: Games to Play on Any Day', 'Pajama Sam, star of award-winning adventure games, continues to challenge and inspire kids. Now your child can play all day, the superhero way, with a collection of 10 unique play experiences based on classic games and puzzles.', '2014-09-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10652, 'Pajama Sam''s Sock Works', 'Climb in and help Pajama Sam control the Sock-O-Matic Mark III, the sock-sorting dream machine the makes chores fun!
INSERT INTO games (id, name, summary, release) VALUES (10651, 'Pajama Sam''s Lost & Found', 'Climb aboard and join Pajama Sam as he jumps and glides through wild worlds, collecting toys, and dodging dangers along the way!
INSERT INTO games (id, name, summary, release) VALUES (3730, 'Pajama Sam In: No Need to Hide When It''s Dark Outside', null, '1996-10-03') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (10650, 'Pajama Sam 4: Life Is Rough When You Lose Your Stuff!', 'Lost among the piles of clothes and toys is his prized comic book. Donning his red cape, Pajama Sam dives into the chaos only to land in a strange and fantastic world that resembles his messy room. Pajama Sam sets off on a series of adventures in search of his lost comic book, looking for lost socks in Agitator Lake, rounding up dust bunnies at the Dust Bunny Corral, exploring the Spilled Soda Swamp and much more. In the end, Sam finds his comic book and realizes that life wouldn''t be so rough if he put away his stuff! Throughout the story, kids meet lots of interesting new friends and tackle fun challenges that will inspire them to solve problems in creative ways.', null) ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3732, 'Pajama Sam 3: You Are What You Eat From Your Head To Your Feet', null, '2000-03-31') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (3731, 'Pajama Sam 2: Thunder and Lightning Aren''t so Frightening', 'Join Pajama Sam, the world''s youngest superhero as he journeys to World Wide Weather, the storm-stirring factory in the sky! Sam''s spent on to many evenings cowering under the couch, so he sets out to conquest his fear of thunderstorms. Just as our little guy begins to learn that storms aren''t as bad as they seem, Sam accidentally send the weather system into a frenzy. Now, there is snow in Saigon, and sunshine in Seattle! He''ll need your child''s help to explore this wonderful world filled with challenging puzzles, fun games, and fantastic new friends as they help Thunder and Lightning fix the broken machines and return the world''s weather to normal before Mother Nature finds out!', '1998-10-01') ON CONFLICT DO NOTHING;
