INSERT INTO games (id, name, summary, release) VALUES (1789, 'Painkiller: Hell & Damnation', 'Painkiller: Hell &amp; Damnation is both remake and sequel of the first-person shooter video game Painkiller, developed by The Farm 51 and published by Nordic Games.', '2012-10-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (14805, 'Painkiller: Black Edition', 'Painkiller Black Edition includes the expansion pack Battle Out of Hell, featuring 10 additional single-player levels and many new villans.', '2004-04-11') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (1835, 'Painkiller: Battle out of Hell', null, '2004-11-30') ON CONFLICT DO NOTHING;
INSERT INTO games (id, name, summary, release) VALUES (828, 'Painkiller', 'Stranded in a place between Heaven and Hell, your time of judgment is at hand. The Underworld is on the verge of unholy war, and you are but a pawn in the infernal battle. As you fight for your purification, the truths behind the deceptions are revealed. Painkiller puts you in a nightmarish world where monsters swarm you in mobs. Eliminate your enemies using a variety of weapons and using your power to morph into a possessed creature.', '2004-04-11') ON CONFLICT DO NOTHING;
